    $nomeNormalizado = normalizeString($nome);
    
    $pessoa = $indexNormalizado[$nomeNormalizado] ?? null;
    
    if (!$pessoa) {
        $naoEncontrados[] = $nome;
        continue;
    }
    
    if (empty($pessoa->cpf)) {
        $semCpf[] = $nome;
    }
    
    if (empty($pessoa->pis_pasep)) {
        $semPis[] = $nome;
    }
    
    if ($pessoa->employeeRegistrations->isEmpty()) {
        $semMatricula[] = $nome;
    } else {
        $temMatricula = false;
        foreach ($pessoa->employeeRegistrations as $reg) {
            if (!empty($reg->matricula)) {
                $temMatricula = true;
                break;
            }
        }
        if (!$temMatricula) {
            $semMatricula[] = $nome;
        }
    }
}

$encontrados = $totalNomes - count($naoEncontrados);

echo "Funcionários encontrados no banco: $encontrados de $totalNomes\n\n";

echo "===========================================\n";
echo "NÃO ENCONTRADOS NO BANCO (" . count($naoEncontrados) . "):\n";
echo "===========================================\n";
foreach ($naoEncontrados as $nome) {
    echo "  ❌ $nome\n";
}

echo "\n===========================================\n";
echo "SEM CPF (" . count($semCpf) . "):\n";
echo "===========================================\n";
foreach ($semCpf as $nome) {
    echo "  ⚠️  $nome\n";
}

echo "\n===========================================\n";
echo "SEM PIS (" . count($semPis) . "):\n";
echo "===========================================\n";
foreach ($semPis as $nome) {
    echo "  ⚠️  $nome\n";
}

echo "\n===========================================\n";
echo "SEM MATRÍCULA (" . count($semMatricula) . "):\n";
echo "===========================================\n";
foreach ($semMatricula as $nome) {
    echo "  ⚠️  $nome\n";
}

echo "\n===========================================\n";
echo "RESUMO:\n";
echo "===========================================\n";
echo "Total na lista:        $totalNomes\n";
echo "Encontrados no banco:  $encontrados\n";
echo "Não encontrados:       " . count($naoEncontrados) . "\n";
echo "Sem CPF:               " . count($semCpf) . "\n";
echo "Sem PIS:               " . count($semPis) . "\n";
echo "Sem Matrícula:         " . count($semMatricula) . "\n";
echo "===========================================\n";
